function largestPalindromeProduct(n) {
  // Good luck!
  let a = parseInt('1'+'0'.repeat(n-1));
  let b = parseInt('9'.repeat(n));  
  console.log(a,b);
  let max=-1;
  for(let i=b;i>=a;i--){
    for(let j=b;j>=a;j--){
      let v=i*j;
      v=v.toString();
      if(v==v.split("").reverse().join("")){
        // console.log(typeof parseInt(v));
        if(parseInt(v)>max){
          max=parseInt(v);
          // console.log(max);
        }
      }
    }
  }
  // console.log(max);
  return max;
}

largestPalindromeProduct(3);
