function gcd(a, b) {
    if (b) {
        return gcd(b, a % b);
    } else {
        return Math.abs(a);
    }
}

function smallestMult(n) {
  // Good luck!
  // let ans=1;
  let lcm=1;
  for(let i=2;i<=n;i++){
    lcm=(lcm*i)/gcd(lcm,i);
    // console.log(lcm);
  }
  return lcm;
}

