function isPime(n){
  if(n<=1) return false;
  if(n<=3) return true;

  if(n%2==0 || n%3==0) return false;

  for(let i=5;i*i<=n;i+=6){
    if(n%i==0 || n%(i+2)==0)
      return false;
  }
  return true;
}

function nthPrime(n) {
  let c=0,i=1;
  while(c<n){
    if(isPime(i)){ 
      c++;
      // console.log(i,c);
      };
    i++;
  }

  return i-1;
}

